import React from "react";
import HeroImg from "../assets/images/hero.png";
import PText from "./PText";
import Button from "./Button";
import SocialMediaArrow from "../assets/images/social-media-arrow.svg";
import ScrollDownArrow from "../assets/images/scroll-down-arrow.svg";
import styled from "styled-components";


const HeroStyles = styled.div`
  .hero{
    height: 100vh;
    min-height: 1000px;
    width: 100%;
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
  }
  .hero__heading{
    font-size: 2rem;
    margin-bottom: -4rem;
    position: relative;
    span {
      display: inline-block;
      width: 100%;
    }
    .hero__name {
      font-family: 'Montserrat SemiBold';
      font-size: 7rem;
      color: var(--white);
    }
  }

  .hero__img {
    max-width: 900px;
    width: 100%;
    height: 600px;
    margin: 0 auto;
    border: 2px solid var(--gray-1);
  }

  .hero__info{
    margin-top: -18rem;

  }

  .hero__social,
  .hero__scrollDown{
    display: flex;
    flex-direction: column;
    gap: 2rem;
    position: absolute;
    bottom: 20px;
    width: 50px;
  }
  .hero__social{
    left: 50px;

  }
  .hero__scrollDown{
    right: 50px;
  }
  .hero__social__indicator,
  .hero__scrollDown{
    width: 50px;
    p{
      font-size: 1.6rem;
      transform: translateY(-70px, rotate(90deg));
      letter-spacing: 0.7rem;
      text-transform: uppercase;
    }

    img{
      max-height: 45px;
      width: 16px;
      margin: 0 auto;
      object-fit: contain;
    }

  }
  .hero__scrollDown{
    img{
      max-height: 70px;
    }
  }
  .hero__social__text{
    ul{
      li{
        margin-bottom: 1rem;
      }
      a{
        display: inline-block;
        font-size: 1.6rem;
      }
    }
  }


`;
export default function HeroSection() {
  return (
    <HeroStyles>
    <div className="hero">
    <div>
      <div className="container">
        <h1 className="hero__heading">
          <span> Hello, This is </span>
          <span className="hero__name">Ferrin Threatt</span>
        </h1>
        <div className="hero__img">
          <img src={HeroImg} alt="" />
        </div>
        <div className="hero__info">
          <PText>
            I am 21 year old, upcoming freelance web developer. Currently, I am
            a junior computer science major at Clemson University. I love to
            design and make new web applications for my clients.
          </PText>
          <Button btnLink="/projects" btnText="see my works" />
          <div className="hero__social">
            <div className="hero__social__indicator">
              <p>Follow</p>
              <img src={SocialMediaArrow} alt="social media arrow" />
            </div>
          </div>
          <div className="hero__social__text">
            <ul>
              <li>
                <a href="#" target="_blank" rel="noreferrer">
                  Twitter
                </a>
              </li>

              <li>
                <a href="#" target="_blank" rel="noreferrer">
                  Instagram
                </a>
              </li>
            </ul> 
          </div>
        </div>
        <div className="hero_scrollDown">
          <p>Scroll</p>
          <img src={ScrollDownArrow} alt=""/>
        </div>
      </div>
    </div>
    </div>
    </HeroStyles>
  );
}
