import React from "react";
import HeroImg from "../assets/images/hero.png";
import PText from "./PText";
import Button from "./Button";
import SocialMediaArrow from "../assets/images/social-media-arrow.svg";
import ScrollDownArrow from "../assets/images/scroll-down-arrow.svg";
import styled from "styled-components";


const HeroStyles = styled.div`
  .hero{
    height: 100vh;
    min-height: 1000px;
    width: 100%;
    text-align: center;
    display: flex;
    justify-content: center;
    position: relative;
  }
  .hero__heading{
    font-size: 2rem;
    margin-bottom: -4rem;
    position: relative
  }


`;
export default function HeroSection() {
  return (
    <HeroStyles>
    <div>
      <div className="container">
        <h1 className="hero__heading">
          <span> Hello, This is </span>
          <span>Ferrin Threatt</span>
        </h1>
        <div className="hero__img">
          <img src={HeroImg} alt="" />
        </div>
        <div className="hero__info">
          <PText>
            I am 21 year old, upcoming freelance web developer. Currently, I am
            a junior computer science major at Clemson University. I love to
            design and make new web applications for my clients.
          </PText>
          <Button btnLink="/projects" btnText="see my works" />
          <div className="hero__social">
            <div className="hero__social__indicator">
              <p>Follow</p>
              <img src={SocialMediaArrow} alt="social media arrow" />
            </div>
          </div>
          <div className="hero_social_text">
            <ul>
              <li>
                <a href="#" target="_blank" rel="noreferrer">
                  Twitter
                </a>
              </li>

              <li>
                <a href="#" target="_blank" rel="noreferrer">
                  Instagram
                </a>
              </li>
            </ul> 
          </div>
        </div>
        <div className="hero_scrollDown">
          <p>Scroll</p>
          <img src={ScrollDownArrow} alt=""/>
        </div>
      </div>
    </div>
    </HeroStyles>
  );
}
